import React, {useState, useEffect} from 'react'

import {MdShoppingCart} from 'react-icons/md'

import {connect} from 'react-redux'

import { useDispatch} from 'react-redux'
import { bindActionCreators } from 'redux'
import {ProductList} from './style'

import * as CartActions from '../../store/modules/cart/actions'

import axios from 'axios'
import {formatPrice} from '../../util/format'


const Home = ({amount}) => {

    const [products, setProducts] = useState([])
    // const [ amount, setAmount] = useState([])
    const dispatch = useDispatch()


    // const handleAmount = ({setAmount}) =>{
    //     setAmount()
    // }


    useEffect(() => {
       async function load(){
           const response = await axios.get('http://localhost:3333/products')

           const data = response.data.map(item => ({
            ...item,
            priceFormatted: formatPrice(item.price),
        }))
           setProducts(data) 


       }
       load() 
    })



        return (

            <ProductList>
                {products.map(item => (
                    <li key={item.id}>
                    <img src={item.image} alt={item.title}/>
                    <strong>{item.title}</strong>
                    <span>{item.priceFormatted}</span>
                    <button type='button' onClick={() => dispatch(CartActions.addToCart(item))}>
                        <div>
                            <MdShoppingCart size={16} color='#fff'/> {amount[products.id] || 0}
                            <span>Adicionar ao Carrinho</span>   
                        </div>
                    </button>
                </li>
                ))}
            </ProductList>
        )

    }



    const mapStateToProps = state => ({
        amount: state.cart.reduce((amount, products) => {
            amount[products.id] = products.amount 

            return amount
        }, {}), 
    })  

    const mapDispatchToProps = dispatch => 
        bindActionCreators(CartActions, dispatch)


    export default connect(mapDispatchToProps, mapStateToProps)(Home)
