const fs = require("fs");

function deleteIfExist(path) {
    if (fs.existsSync(path)) {
        fs.unlinkSync(path);
    }
}

function writeJson(path, obj) {
    deleteIfExist(path);
    fs.writeFileSync(path, JSON.stringify(obj, null, 4));
}

function readJson(path) {
    let ret = null;
    if (fs.existsSync(path)) {
        ret = fs.readFileSync(path, "utf8");
        ret = JSON.parse(ret);
    }
    return ret;
}


module.exports = {writeJson, readJson};
