const assert = require("assert");
const {readJson, writeJson} = require("../fileops");


describe('json', function () {

    const dbpath = "./tests/db.json";

    it('should read and write', function () {

        writeJson(dbpath, {user: "user1", pass: "1234pass"});
        const res = readJson(dbpath);
        // console.log(res);
        assert.deepStrictEqual(res, {user: 'user1', pass: '1234pass'});
    });
});
